import { getCast } from 'fetch';
import { useEffect, useState } from 'react';
import { useParams } from 'react-router';

export default function Cast() {
  const { movieId } = useParams();
  const [cast, setCast] = useState(null);
  useEffect(() => {
    const fetchedEl = async () => {
      try {
        const item = await getCast(movieId);
        setCast(item.data);
      } catch (error) {
        console.log(error);
      }
    };
    fetchedEl();
  }, [movieId]);

  return (
    <ul>
      {cast &&
        cast.cast.map(item => (
          <li key={item.id}>
            <img src={`https://image.tmdb.org/t/p/w180/${item.profile_path}`} />
            <p>{item.name}</p>
            <p>{item.character}</p>
          </li>
        ))}
    </ul>
  );
}
