import { getReviews } from 'fetch';
import { useEffect, useState } from 'react';
import { useParams } from 'react-router';
import { toast } from 'react-hot-toast';
export default function Reviews() {
  const { movieId } = useParams();
  const [reviews, setRewievs] = useState(0);
  useEffect(() => {
    const fetchedEl = async () => {
      try {
        const item = await getReviews(movieId);
        setRewievs(item.data);
      } catch (error) {
        console.log(toast.error('Something Went Wrong'));
      }
    };
    fetchedEl();
  }, [movieId]);

  return (
    <div>
      {reviews.total_results > 0 ? (
        reviews.results.map(item => (
          <li key={item.id}>
            <h2>{item.author}</h2>
            <p>{item.content}</p>
          </li>
        ))
      ) : (
        <p>Sorry we dont have any reviews </p>
      )}
    </div>
  );
}
