import { Outlet, useLocation, useParams } from 'react-router';
import { getId } from 'fetch';
import { Suspense, useEffect, useRef, useState } from 'react';
import { NavLink } from 'react-router-dom';
export default function MovieDetails() {
  const location = useLocation();
  const back = useRef(location.state?.from ?? '/');

  const { movieId } = useParams();
  const [film, setfilm] = useState(null);
  useEffect(() => {
    const fetchedEl = async () => {
      try {
        const item = await getId(movieId);
        setfilm(item.data);
      } catch (error) {
        console.log(error);
      }
    };
    fetchedEl();
  }, [movieId]);

  return (
    <div>
      <NavLink to={back.current}>Go back</NavLink>
      {film && (
        <div>
          <img src={`https://image.tmdb.org/t/p/w200/${film.poster_path}`} />
          <p>{film.title}</p>
        </div>
      )}
      <NavLink to="cast">Cast</NavLink>
      <NavLink to="reviews">Reviews</NavLink>
      <Suspense fallback={<div>Loading...</div>}>
        <Outlet />
      </Suspense>
    </div>
  );
}
