import { Outlet, useLocation, useParams } from 'react-router';
import { getId } from 'fetch';
import { Suspense, useEffect, useRef, useState } from 'react';
import { NavLink } from 'react-router-dom';
import styled from 'styled-components';
const MovieStyled = styled.div`
  display: flex;
  gap: 15px;
  margin-bottom: 10px;
`;
const WrapperStyled = styled.div`
  display: flex;
  flex-direction: column;
  gap: 15px;
`;
const StyledLink = styled(NavLink)`
  color: blue;
  margin-right: 8px;
  &:hover {
    color: orange;
  }
`;
export default function MovieDetails() {
  const location = useLocation();
  const back = useRef(location.state?.from ?? '/');

  const { movieId } = useParams();
  const [film, setfilm] = useState(null);
  useEffect(() => {
    const fetchedEl = async () => {
      try {
        const item = await getId(movieId);
        setfilm(item.data);
      } catch (error) {
        console.log(error);
      }
    };
    fetchedEl();
  }, [movieId]);

  return (
    <div>
      <NavLink to={back.current}>Go back</NavLink>
      {film && (
        <MovieStyled>
          <img src={`https://image.tmdb.org/t/p/w200/${film.poster_path}`} />
          <WrapperStyled>
            <h1>{film.title}</h1>
            <h2>Overview</h2>
            <p>{film.overview}</p>
            <h2>Genres</h2>
            <MovieStyled>
              {film.genres.map(item => (
                <p key={item.id}>{item.name}</p>
              ))}
            </MovieStyled>
          </WrapperStyled>
        </MovieStyled>
      )}
      <WrapperStyled>
        Additional information <StyledLink to="cast">Cast</StyledLink>
        <StyledLink to="reviews">Reviews</StyledLink>
      </WrapperStyled>
      <Suspense fallback={<div>Loading...</div>}>
        <Outlet />
      </Suspense>
    </div>
  );
}
