import { getByName } from 'fetch';
import { useEffect, useState } from 'react';
import { NavLink, useSearchParams } from 'react-router-dom';

export default function Movies() {
  const location = useLocation();
  const back = useRef(location.state?.from ?? '/');
  const [searchParams, setSearchParams] = useSearchParams();
  const [films, setfilm] = useState('');
  function search(evnt) {
    evnt.preventDefault();
    const nameFilm = evnt.target.elements.searchValue.value;
    if (nameFilm === '') {
      return setSearchParams({});
    }
    setSearchParams({ query: nameFilm });

    evnt.target.reset();
  }
  useEffect(() => {
    if (searchParams.get('query')) {
      const fetchedEl = async () => {
        try {
          const item = await getByName(searchParams.get('query'));
          setfilm(item.data);
        } catch (error) {
          console.log(error);
        }
      };
      fetchedEl();
    }
  }, [searchParams]);
  return (
    <div>
      <form onSubmit={search}>
        <input type="text" name="searchValue" />
        <button type="submit">Search</button>
      </form>
      <ul>
        {films &&
          films.results.map(item => {
            return (
              <NavLink to={`${item.id}`} key={item.id}>
                {item.title}
              </NavLink>
            );
          })}
      </ul>
    </div>
  );
}
